export default function Users() {

const [users, setUsers] = useState({});
const [page, setPage] = useState(setGetParams('get') || 1);
const countPerPage = 6;

const getUser = async () => {
  axios.get(`https://reqres.in/api/users?page=${page}&per_page=${countPerPage}`).then(res => {
    setUsers(res.data);
  }).catch(err => {
    setUsers({});
  });
}

function setGetParams(type) {
  var queryParams = new URLSearchParams(window.location.search);
  if(type === 'get'){
    return(queryParams.get("page"));
  }

  queryParams.set("page",page);
  window.history.replaceState(null, null, "?"+queryParams.toString());
}

useEffect(() => {
  getUser();
  setGetParams();
}, [page]);

return (
  <div className="App">
            <ReactTooltip place="left" type="success" effect="solid" />

    <DataTable
      columns={columns}
      data={users.data}
      pagination
      paginationServer
      paginationTotalRows={users.total}
      paginationPerPage={countPerPage}
      paginationIconFirstPage
      paginationIconLastPage
      paginationComponentOptions={{
        noRowsPerPage: true
      }}
      onChangePage={page => setPage(page)}
    />
    {/* <Tooltip/>     */}
  </div>
);
}
